package com.example.myapplication;import android.widget.Toast;import androidx.appcompat.app.AppCompatActivity;import android.os.Bundle;import android.view.View;import android.widget.Button;import android.widget.EditText;public class MainActivity2 extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main2);        EditText uname = findViewById(R.id.uname);        EditText upwd1 = findViewById(R.id.upwd1);        Button b2=findViewById(R.id.verify);        b2.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                String mid = uname.getText().toString();                String pa = upwd1.getText().toString();                if (mid.equals("PSG") && pa.equals("psg")) {                    Toast t = Toast.makeText(getApplicationContext(), "Username and password are correct", Toast.LENGTH_LONG);                    t.show();                } else {                    Toast t = Toast.makeText(getApplicationContext(), "Username or password is incorrect", Toast.LENGTH_LONG);                    t.show();                }            }        });    }}
