package com.example.myapplication;import androidx.appcompat.app.AppCompatActivity;import android.graphics.Bitmap;import android.graphics.Canvas;import android.graphics.Color;import android.graphics.Paint;import android.graphics.drawable.BitmapDrawable;import android.os.Bundle;
import android.view.View;import android.widget.Button;import android.widget.ImageView;public class MainActivity3 extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main3);        //Creating a bitmap        Bitmap bg = Bitmap.createBitmap(720,1280,Bitmap.Config.ARGB_8888);        ImageView iv = findViewById(R.id.imageView);        iv.setBackground(new BitmapDrawable(bg));        //creating a canvas        Canvas canvas = new Canvas(bg);        //creating rectangle        Paint paint = new Paint();        paint.setColor(Color.MAGENTA);        paint.setTextSize(70);        canvas.drawText("Rectangle", 400, 150, paint);        canvas.drawRect(400, 200, 650, 700, paint);        //To draw a Square        Paint paint1 = new Paint();        paint1.setColor(Color.BLUE);        paint1.setTextSize(80);        canvas.drawText("Square", 90, 770, paint1);        canvas.drawRect(100, 800, 250, 1000, paint1);        //To draw a Circle        Paint paint2 = new Paint();        paint2.setColor(Color.GRAY);        paint2.setTextSize(70);        canvas.drawText("Circle", 100, 150, paint2);        canvas.drawCircle(200, 350, 150, paint2);        //To draw a Line        Paint paint3 = new Paint();        paint3.setColor(Color.BLUE);        paint3.setTextSize(80);        canvas.drawText("Line", 500, 900, paint3);        canvas.drawLine(520, 850, 550, 1150, paint3);
        Button btn = findViewById(R.id.btn);        btn.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                iv.setBackground(null);                Bitmap bg1 = Bitmap.createBitmap(720,1280,Bitmap.Config.ARGB_8888);                iv.setBackground(new BitmapDrawable(bg1));                Canvas canvas1 = new Canvas(bg1);                Paint p1 = new Paint();                p1.setColor(Color.BLUE);                p1.setTextSize(70);                canvas1.drawText("TEDDY", 250, 130, p1);                canvas1.drawCircle(400,400,150,p1);                Paint p2 = new Paint();                p2.setColor(Color.RED);                canvas1.drawCircle(400,780,250,p2);            }        });    }}
