package com.example.threading;import androidx.appcompat.app.AppCompatActivity;import android.os.Bundle;import android.view.View;import android.widget.Button;import android.widget.EditText;import android.widget.TextView;public class MainActivity extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        TextView tvt = findViewById(R.id.textView);        TextView tvnum = findViewById(R.id.textView2);        Button text = findViewById(R.id.button1);        Button num = findViewById(R.id.button2);        text.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {            }        });        num.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                Runnable r = new Runnable() {                    @Override                    public void run(){                        for(int i=0;i < i+1;i++) {                            final int j = i;                            try {                                Thread.sleep(1000);                                tvt.setTextSize(30);                                tvt.setText(j+" ");                            } catch (Exception e){
                                e.printStackTrace();                            }                            tvt.post(new Runnable() {                                @Override                                public void run() {                                    tvt.setTextSize(30);                                    tvt.setText(j+" ");                                }                            });                        }                    }                };                Thread t1 = new Thread(r);                t1.start();            }        });        text.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                Runnable r = new Runnable() {                    @Override                    public void run(){                        for(int i=20;i<i+1;i++) {                            final int j = i;                            try {                                Thread.sleep(5000);                                tvnum.setTextSize(j);                                tvnum.setText("Thread!!");                            } catch (Exception e){                                e.printStackTrace();                            }                            tvt.post(new Runnable() {                                @Override                                public void run() {                                    tvnum.setTextSize(j);                                    tvnum.setText("Thread!!");                                }                            });                        }                    }                };                Thread t2 = new Thread(r);                t2.start();            }        });    }}
