package com.example.notification;import androidx.appcompat.app.AppCompatActivity;import androidx.core.app.NotificationCompat;import androidx.core.app.NotificationManagerCompat;import android.app.Notification;import android.app.NotificationChannel;import android.app.NotificationManager;import android.content.Context;import android.os.Build;import android.os.Bundle;import android.view.View;import android.widget.Button;public class MainActivity extends AppCompatActivity {    Button b1,b2;    @Override    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        b1=findViewById(R.id.button);        b2=findViewById(R.id.button2);        b1.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                addNotification1();            }        });        b2.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                addNotification2();            }        });    }    private void addNotification1()    {        if(Build.VERSION.SDK_INT>=Build.VERSION_CODES.O)        {            final String CHANNEL_ID = "HEADS_UP_NOTIFICATIONS";            NotificationChannel channel=new NotificationChannel(CHANNEL_ID,"MyNotification",NotificationManager.IMPORTANCE_HIGH);            getSystemService(NotificationManager.class).createNotificationChannel(channel);            Notification.Builder notification=new Notification.Builder(this, CHANNEL_ID)                    .setContentTitle("Hello Title")                    .setContentText("This is First button Notification")                    .setSmallIcon(R.drawable.ic_launcher_background)                    .setAutoCancel(true);            NotificationManagerCompat.from(this).notify(1,notification.build());        }        else        {            String NOTIFICATION_CHANNEL_ID ="my_channel_id_01";            NotificationCompat.Builder notificationBuilder =new NotificationCompat.Builder(this, NOTIFICATION_CHANNEL_ID)                    .setContentTitle("Hello Title")                    .setContentText("This is First button Notification")                    .setSmallIcon(R.drawable.ic_launcher_background)                    .setAutoCancel(true);            NotificationManager notificationManager=(NotificationManager)                    getSystemService(Context.NOTIFICATION_SERVICE);            notificationManager.notify(1,notificationBuilder.build());        }    }    private void addNotification2()    {        if(Build.VERSION.SDK_INT>=Build.VERSION_CODES.O)        {            final String CHANNEL_ID2 = "HEADS_UP_NOTIFICATIONS2";            NotificationChannel channel2=new NotificationChannel(CHANNEL_ID2,"MyNotification2",NotificationManager.IMPORTANCE_DEFAULT);            getSystemService(NotificationManager.class).createNotificationChannel(channel2);            Notification.Builder notification=new Notification.Builder(this, CHANNEL_ID2)                    .setContentTitle("Hello Title")                    .setContentText("This is Second button Notification")                    .setSmallIcon(R.drawable.ic_launcher_background)                    .setAutoCancel(true);            NotificationManagerCompat.from(this).notify(2,notification.build());
        }        else        {            String NOTIFICATION_CHANNEL_ID2 ="my_channel_id_02";            NotificationCompat.Builder notificationBuilder =new NotificationCompat.Builder(this, NOTIFICATION_CHANNEL_ID2)                    .setContentTitle("Hello Title")                    .setContentText("This is Second button Notification")                    .setSmallIcon(R.drawable.ic_launcher_background)                    .setAutoCancel(true);            NotificationManager notificationManager=(NotificationManager)                    getSystemService(Context.NOTIFICATION_SERVICE);            notificationManager.notify(2,notificationBuilder.build());        }    }}
