package com.example.expt8;import androidx.appcompat.app.AppCompatActivity;import android.os.Bundle;import android.os.Environment;import android.view.View;import android.widget.Button;import android.widget.EditText;import android.widget.TextView;import android.widget.Toast;import java.io.BufferedReader;import java.io.File;import java.io.FileOutputStream;import java.io.FileReader;public class MainActivity extends AppCompatActivity {    String filename = "Sample.txt";    String filepath = "fileDir";    String filecontent = "";    Button read, write;    TextView txt;    EditText msg;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        write = (Button) findViewById(R.id.button);        read = (Button) findViewById(R.id.button2);        txt = (TextView) findViewById(R.id.textView);        msg = (EditText) findViewById(R.id.editTextTextPersonName);        if(!isWriteable())        {            write.setEnabled(false);        }        write.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                txt.setText("");                filecontent=msg.getText().toString().trim();                if(!filecontent.equals(""))                {                    File Extfile = new File(getExternalFilesDir(filepath),filename);
                    try                    {                        FileOutputStream fos = new FileOutputStream(Extfile,true);                        fos.write(filecontent.getBytes());                        fos.write('\n');                    }                    catch(Exception e)                    {                        e.printStackTrace();                    }                    msg.setText("");                    Toast.makeText(MainActivity.this,"Write content Successfully",Toast.LENGTH_LONG).show();                }                else                    Toast.makeText(MainActivity.this,"Cannot be empty",Toast.LENGTH_LONG).show();            }        });        read.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                FileReader fr = null;                File Extfile = new File(getExternalFilesDir(filepath),filename);                StringBuilder sb = new StringBuilder();                try                {                    fr = new FileReader(Extfile);                    BufferedReader br = new BufferedReader(fr);                    String line = br.readLine();                    while(line!=null)                    {                        sb.append(line).append('\n');                        line = br.readLine();                    }                }                catch (Exception e1) {                    e1.printStackTrace();                }                finally{                    String fc = "File Content\n"+sb.toString();                    txt.setText(fc);                }            }        });    }    public boolean isWriteable()    {        String ext_storage = Environment.getExternalStorageState();        if(ext_storage.equals(Environment.MEDIA_MOUNTED))        {            return true;        }        else            return  false;    }}
